  }
}

function App() {
  return (
    <div className="App">
      {/* This is where the patient information will be displayed*/}
      <header className="patient-info">
        <h2>Patient name</h2>
        <ISBARSimpleInputField />
      </header>

    </div>
  );
}



export default App;
